
function selectTab(name){let tabs=document.getElementsByClassName("tabs");for(let i=0,len=tabs.length;i<len;i++){if(tabs[i].name==name)tabs[i].classList.add("selected");else tabs[i].classList.remove('selected');}
let tabcontent=document.getElementsByClassName("tabcontent");for(let i=0,len=tabcontent.length;i<len;i++){if(tabcontent[i].id==name)tabcontent[i].style.display="block";else tabcontent[i].style.display="none";}}
document.addEventListener('DOMContentLoaded',function(){let b=document.getElementsByClassName('tabcontent');for(let i=0;i<b.length;i++)b[i].style.display="none";document.getElementById("openFile").onclick=function(){document.getElementById("loadFile").click()};document.getElementById("loadFile").onchange=function(){anova.open();};});